com.cloudbees.plugins.flow.BuildFlow.dsl:
build_job = build ( "Monitoring-NGSIAdapter-1-Build", REPOSITORY: params['REPOSITORY'], BRANCH: params['BRANCH'], WORKSPACE: params['WORKSPACE'] )
git_curr_commit = build_job.environment.get( "GIT_COMMIT" )
git_prev_commit = build_job.environment.get( "GIT_PREVIOUS_COMMIT" )

if ( git_curr_commit == git_prev_commit ) {
	out.println "No changes in SCM (latest build commit $git_prev_commit). Finishing..."
} else {
	parallel (
	    { build ( "Monitoring-NGSIAdapter-2-Package-deb", REPOSITORY: params['REPOSITORY'], BRANCH: params['BRANCH'] ) },
	    { build ( "Monitoring-NGSIAdapter-2-Package-rpm", WORKSPACE: params['WORKSPACE'] ) }
	)
	build ( "Monitoring-NGSIAdapter-3-Deploy", WORKSPACE: params['WORKSPACE'], ENVIRONMENT: params['ENVIRONMENT'] )
	build ( "Monitoring-NGSIAdapter-4-Acceptance-comp", WORKSPACE: params['WORKSPACE'], ENVIRONMENT: params['ENVIRONMENT'] )
}
